import React from 'react';
import { useParams } from 'react-router-dom';

export default function JournalDetailPage({ journals }) {
  const { journalId } = useParams();
  const journal = journals.filter((j) => j.id === journalId);

  if (!journal) {
    return <p>Journal not found!!!</p>;
  }
  return (
    <>
      <header>
        <button>back</button>
        <label>{journal.date}</label>
      </header>
      <section>
        <label>{journal.title}</label>
        <p>{journal.content}</p>
      </section>
      <section>
        <button>delete</button>
        <button>edit</button>
      </section>
    </>
  );
}
